#include <bits/stdc++.h>
using namespace std;


// } Driver Code Ends
class Solution
{
    public:
    //Function to count the number of possible triangles.
    int findNumberOfTriangles(int arr[], int n)
    {
        int ans = 0;
        
        sort(arr, arr + n);
        
        for(int i = n - 1; i >= 2; i--){
            int st = 0;
            int ed = i - 1;
            
            while(st < ed){
                if((arr[st] + arr[ed]) > arr[i]){
                    ans += (ed - st);
                    ed--;
                }
                else{
                    st++;
                }
            }
        }
        
        return ans;
    }
};


