#include <bits/stdc++.h>
using namespace std;

// } Driver Code Ends
class Solution
{
public:    
    
    int transitionPoint(int arr[], int n) {
        // code here
        int l=0,h=n-1,ind=-1;
        while(l<=h){
            int mid=(l+h)/2;
            if(arr[mid]==1){
                ind=mid;
                h=mid-1;
            }
            else if(arr[mid]<1)
                l=mid+1;
        }
        return ind;
    }
};

