class Solution
{
    

    int SubsequenceLength(String s) 
    {
        // Code here  
        int n=s.length();
        int ml=0;
        int cl=0;
        int[] f=new int[26];
        int i=0,j=0;
        while(i<n){
            if(f[s.charAt(i)-'a']==0){
                f[s.charAt(i)-'a']++;
                i++;
                cl++;
