import java.util.*;

class Maxsum_Among_All_Rotations_Array
{
	public  static void main(String args[])
	{
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		while(t>0)
		{
			int n = sc.nextInt();
			int arr[] = new int[n];
			for(int i=0;i<n;i++)
				arr[i] = sc.nextInt();
			
			System.out.println(new GfG().max_sum(arr,n));
			
		t--;
		}
	}
	
}

// } Driver Code Ends


class GfG
{
    int max_sum(int A[], int n)
    {
// Your code here
int sum = 0;
int res = 0;
int max = 0;
for(int i = 0 ; i< n ; i++)
{
res = res + A[i]*i;
sum = sum + A[i];
}
for(int i = 0 ; i< n ; i++)
{
    res = res + A[i]*n - sum;
   max = Math.max(res , max);
}
return max;
    }
}

