	int getLevelDiff(Node root)
	{
	    int diff =0;
        if(root==null)return diff;
        Queue<Node> mq = new LinkedList<>();
        Queue<Node> hq = new LinkedList<>();
        mq.add(root);
        int oddSum =0, evenSum=0;
        boolean flag = true;
        while(mq.size()>0){
            //remove the nodes
            root = mq.remove();
            if(flag)oddSum+=root.data;
            else evenSum+=root.data;
            //add left child in hq if exist
            if(root.left!=null)hq.add(root.left);
            //add right child in hq if exist
            if(root.right!=null)hq.add(root.right);
            if(mq.size()==0){
                flag = !flag;
                mq = hq;
                hq = new LinkedList<>();
            }
        }
        return oddSum-evenSum;
