class Solution:    
    def rearrangeEvenOdd(self, head):
        #code here

        if not head or not head.next:
          return head
    
        odd = head
        even = head.next
        evenHead = even  # To reconnect the even list back to the odd list later
    
        while even and even.next:
            # Safely advance the odd nodes
            if odd.next:
                odd.next = odd.next.next
                odd = odd.next if odd.next else odd
            
            # Safely advance the even nodes
            if even.next:
                even.next = even.next.next
                even = even.next if even.next else even
    
        # Connect the last odd node to the head of the even list
        if odd:
            odd.next = evenHead
    
        return head
