class Solution 
{
    //Function to reverse words in a given string.
    String reverseWords(String S)
    {
        String[] ans=S.split("\\.");
        int i=ans.length-1;
        String s="";
        while(i>=0)
        {
            s=s+ans[i];
            if(i!=0)
            {
                s=s+".";
            }
            i--;
            
        }
        return s;
        
    }
}

