class Solution
{
    public:
    
    static bool check(int i, int j, int n, int m,int x, vector<vector<int> > & matrix){
        if (matrix[i][j] == x)
            return true;
            
        matrix[i][j] = -1;
        
        if (i+1<n && matrix[i+1][j]<=x && matrix[i+1][j]!= -1)
            return check(i+1, j, n, m, x, matrix);
        else if (i-1>=0 && matrix[i-1][j]>=x && matrix[i-1][j]!= -1)
            return check(i-1, j, n, m, x, matrix);
        else if (j+1<m)
            return check(i, j+1, n, m, x, matrix);
        else
            return false;
        
    }
    //Function to search a given number in row-column sorted matrix.
    bool search(vector<vector<int> > matrix, int n, int m, int x) 
    {
        return check(0, 0, n, m, x, matrix);

    }
};

