class Solution
{
    public:
    //Function to calculate the span of stockâ€™s price for all n days.
    vector <int> calculateSpan(int price[], int n)
    {
        vector<int>ans(n,1);
        stack<int>temp;
        for(int i=n-1;i>=0;i--){
            while(!temp.empty() && price[i]>price[temp.top()]){
                ans[temp.top()]=temp.top()-i;
                temp.pop();
            }
            temp.push(i);
        }
        while(!temp.empty()){
            ans[temp.top()]=temp.top()+1;
            temp.pop();
        }
        return ans;
    }
};



