class Solution {
  public:
    vector<int> bfsOfGraph(int V, vector<int> adj[]) {
        
        vector<int> ans ;
        queue<int> q ;
        vector<int> vis(V , -1) ;
        
        q.push(0) ;
        vis[0] = 1 ;
        
        while(q.size() > 0){
            
            int temp = q.front() ;
            ans.push_back(temp) ;
            q.pop() ;
            
            for(auto i : adj[temp]){
                if(vis[i] != 1){
                    q.push(i) ;
                    vis[i] = 1 ;
                }
            }    
            
        }
        return ans ;
        
    }
};

