class Solution:
    def numberOfPaths(self, m, n):
        mod=10**9+7
        
        dp = [[0] * n for _ in range(m)]
        dp[m - 1][n - 1] = 1
 
        for i in range(m - 1, -1, -1):
            for j in range(n - 1, -1, -1):
                if i == m - 1 and j == n - 1:
                    continue
                if i + 1 < m:
                    dp[i][j] += dp[i + 1][j]%mod # Move down
                if j + 1 < n:
                    dp[i][j] += dp[i][j + 1]%mod # Move right
 
        return dp[0][0]%mod
 

