int countRev (string s)
{
    int len = s.length();
    if(len%2) return -1;
    int c = 0;
    stack<char> st;
    for(int i = 0;i < len;i++)
    {
        if(s[i] == '{'){
            st.push('{');
        }
        else{
            if(st.empty()){
                st.push('{');
                c++;
            }
            else st.pop();
        }
    }
    return c+st.size()/2;
}

