class Solution{
	public:
	long long minimumCandies(int N,int arr[])
{
 //write your code here
    vector<long long> ans1(N,1);
    vector<long long>ans2(N,1);
    for(int i=1;i<N;i++){
        if(arr[i]>arr[i-1]){
            ans1[i]=ans1[i-1]+1;
        }
    }
    for(int i=N-2;i>=0;i--){
        if(arr[i]>arr[i+1]){
            ans2[i]=ans2[i+1]+1;
        }
    }
    long long val=0;
    for(int i=0;i<N;i++){
        val+=(long long)max(ans1[i],ans2[i]);
    }
    return val;
}
};

