    Node* findIntersection(Node* head1, Node* head2)
    {
        unordered_map<int, int> mp;
        
        Node* temp=head2;
        
        while(temp){
            mp[temp->data]++;
            temp=temp->next;
        }
        
        
        Node* list=NULL;
        Node* it=0;
        temp=head1;
        
        while(temp){
            if(mp.find(temp->data) != mp.end()){
                //Node value found in L2
                
                if(!list){
                    list=temp;
                    it=list;
                }else{
                    if(mp[temp->data]>0){
                        it->next=temp;
                        it=it->next;
                        mp[temp->data]--;
                    }
                }
            }
            temp=temp->next;
        }
        
        it->next=NULL;
        
        return list;
    }
};

