class Solution
{
public:
    int dfs(vector<vector<int>>&mat,int i,int j,int n,int m,int t){
      if(i<0||i>=n||j<0||j>=m||mat[i][j]!=t){
         return 0; 
      }
      
      mat[i][j]=-1;
      int s=1;
       s+=dfs(mat,i+1,j,n,m,t);
       s+=dfs(mat,i-1,j,n,m,t);
      s+=dfs(mat,i,j+1,n,m,t);
       s+=dfs(mat,i,j-1,n,m,t);
      
      return s;
    }
    int findBasin(int n,int m,vector<vector<int>>&mat)
    {   int ans=0;
        
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(mat[i][j]>0){
                    int s=dfs(mat,i,j,n,m,mat[i][j]);
                    ans=max(ans,s);
                }
            }
        }
        
        
        return ans;
    }
};

