//User function Template for Java

class Solution {
    int minCut(int[] arr, int n, int b)
    {
        Arrays.sort(arr);
        return func(arr, n, b);
    }
    int func(int[] arr, int n, int b)
    {
        int l = 0, h = arr[n - 1];
        int ans = -1;

        while (l <= h) {
            int m = (l + h) / 2;
            int c = 0;

            for (int i = n - 1; i >= 0; i--) {
                if (arr[i] > m) {
                    c += arr[i] - m;
                } else {
                    break;
                }
            }

            if (c >= b) {
                ans = m;
                l = m + 1;
            } else {
                h = m - 1;
            }
        }

        return ans;
    }
}

