class Solution{
    
    // A[]: input array
    // N: size of array
    // Function to find the maximum index difference.
    static int maxIndexDiff(int r[], int h) { 
        
        // Your code here
        int k=h-1,d=0,u=0;
        int s=r[0];
        int v=r[h-1];
        if(s<v) return h-1;
        while(u<=k){
            if(r[u] <= r[k]){
                d=Math.max(d,k-u);
                u++;
                k=h-1;
                if(u>h/2) return d;
            }
            else k--;
        }
        return d;
    }
}

