class Solution
{
   public:
    int minMoves(int target)
   {
      //Write your code here
      int sum=0;
      int step=0;
      target=abs(target);
      while(sum<target){
          sum+=step;
          step++;
      }
      while((sum-target)%2==1){
          sum+=step;
          step++;
      }
      return step-1;
   }
};


