class Solution:
    def minSteps(self, a, n):
        dp = [float('inf')] * n
        dp[0] = 0
        
        for i in range(1, n):
            for j in range(i):
                if j + a[j] >= i:
                    dp[i] = min(dp[i], dp[j] + 1)
        
        return dp[-1]
