class Solution{


	public:
	long long help(int N,int node,vector<int>adj[],vector<vector<long long>>&dp){
        
        if(N==0) return 1;
        if(dp[N][node]!=-1) return dp[N][node];
        
        long long ans=0;
        for(auto it:adj[node]){
            ans+=help(N-1,it,adj,dp);
        }
        
        return dp[N][node]=ans;
    }
    
