'''
# Node Class:
class Node:
    def init(self,val):
        self.data = val
        self.left = None
        self.right = None
'''
class Solution:
    #Function to return list containing elements of right view of binary tree.
    def rightView(self,root):
        mp = {}
        # code here
        
        def dfs(node, depth, offset):
            if node is None:
                return
            
            if offset in mp:
                if depth > mp[offset][0]:
                    mp[offset] = (depth, node.data)
            else:
                mp[offset] = (depth, node.data)
            
            if node.left is not None:
                dfs(node.left, offset - 1, depth + 1)
            if node.right is not None:
                dfs(node.right, offset + 1, depth + 1)
                
            return
        
        dfs(root, 0, 0)
        
        keys = sorted(list(mp.keys()))
        ans = [mp[k][1] for k in keys]
        
        return ans
