    public:
    //Function to find if there is a celebrity in the party or not.
    int celebrity(vector<vector<int> >& M, int n) 
    {
        // code here
        stack<int> st;
        
        // Pushed all people in stack
        for(int i = 0; i < n; i++) {
            st.push(i);
        }
        
        // Check 2 persons if they know each other
        while(st.size() != 1) {
            int a = st.top();
            st.pop();
            int b = st.top();
            st.pop();
            
            // If A knows B, a is not a celebrity (Discard B)
            if(M[a][b]) {
                st.push(b);
            }
            
            // If B knows A, b is not a celebrity (Discard A)
            else {
                st.push(a);
            }
        }
        
        // One person left, might be a celebrity, Use Brute force to check
        int might = st.top();
        
        for(int i = 0; i < n; i++) {
            if(M[might][i] != 0) {
                return -1;
            }
        }
        
        for(int i = 0; i < n; i++) {
            if(M[i][might] != 1 && might != i) {
                return -1;
            }
        }
        
        return might;
    }
};

