import java.io.*;
import java.util.*;
import java.lang.*; 

class GFG{
    public static void main(String args[]) throws IOException { 
        Scanner sc = new Scanner(System.in);
        int t = sc.nextInt();
        while(t > 0){
        	int n = sc.nextInt();
        	int target = sc.nextInt();
        	int[] array = new int[n];
        	for(int i=0; i<n; i++)
        		array[i] = sc.nextInt();

            Solution ob = new Solution();
            System.out.println(ob.threeSumClosest(array,target));
            t--;
        }
    } 
} 

// } Driver Code Ends


//User function Template for Java

class Solution 
{ 
    static int threeSumClosest(int[] array, int target)  
	{ 
       Arrays.sort(array);
       
       long cs= Integer.MAX_VALUE;
       int n = array.length;
       for(int i=0;i<n-2;i++){
           int p1 = i+1, p2=n-1;
           while(p1<p2){
               int sum = array[i] + array[p1] + array[p2];
               if(Math.abs(target - sum) <= Math.abs(target-cs)){
                   cs = sum;
               }
               if(sum > target){
                   p2--;
               }else{
                   p1++;
               }
           }
       }
       return (int)cs; 
	} 
}

