class Solution{
public:
     int minimize(vector<int> &X, vector<int> &Y, vector<int> &Z) {
        int a=0,b=0,c=0;
        int ans=INT_MAX;
        int diff;
        while(a<X.size()&&b<Y.size()&&c<Z.size())
        {
            diff=max({abs(X[a]-Y[b]),abs(Y[b]-Z[c]),abs(Z[c]-X[a])});
            ans=min(ans,diff);
            if(ans==0)break;
            if(X[a]<=Y[b]&&X[a]<=Z[c])a++;
            else if(Y[b]<=X[a]&&Y[b]<=Z[c])b++;
            else c++;
        }
        return ans;
    }
};

