import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {

    public static void main(String[] args) throws Exception {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int tc = Integer.parseInt(br.readLine().trim());
        while (tc-- > 0) {
            String[] inputLine;
            inputLine = br.readLine().trim().split(" ");
            int n = Integer.parseInt(inputLine[0]);
            int[] arr = new int[n];
            inputLine = br.readLine().trim().split(" ");
            for (int i = 0; i < n; i++) {
                arr[i] = Integer.parseInt(inputLine[i]);
            }

            int ans = new Solution().minChocolates(arr, n);
            System.out.println(ans);
        }
    }
}

// } Driver Code Ends


//User function Template for Java

class Solution {
    int minChocolates(int[] arr, int n) {
        // code here
        int c=1;
        int ans=0;
        if(n==1)return 1;
        for(int i=0; i<n;){
            if(i==n-1){
                if(arr[i]<arr[i-1]){
                    ans+=1;
                    // System.out.print(1+" ");
                }
                else{
                    ans+=c;
                    // System.out.print(c+" ");
                }
                i++;
            }
            else if(i+1<n && arr[i]<arr[i+1]){
                // System.out.print(c+" ");
                ans+=c;
                c++;
                i++;
            }
            else if(i+1<n && arr[i]==arr[i+1]){
                if(i-1>=0 && arr[i]>arr[i-1]){
                    ans+=c;
                    // System.out.print(c+" ");
                    c=1;
                    i++;
                }
                else{
                    c=1;
                    // System.out.print(c+" ");
                    ans+=c;
                    i++;
                }
                
            }
            else{
                int count=1;
                int j=i;
                while(j+1<n && arr[j]>arr[j+1]){
                    j++;
                    count++;
                }
                c=Math.max(c, count);
                ans=ans+c;
                // System.out.print(c+" ");
                c=count-1;
                i++;
                for(int p=i; p<=j; p++){
                    ans=ans+c;
                    // System.out.print(c+" ");
                    c--;
                    i++;
                }
                if(i<n && arr[i-1]==arr[i])c=1;
                else c=2;
            }
        }
        // System.out.println();
        return ans;
    }
}

