class Solution {
  public:
	int solve(vector<vector<int>>& M, int n, int m, int i, int j) {
        if (i < 0 || i >= n || j < 0 || j >= m || M[i][j] == 0) {
            return 0;
        }

        int value = M[i][j]; 
        M[i][j] = 0; 

        int up = solve(M, n, m, i - 1, j);
        int down = solve(M, n, m, i + 1, j);
        int left = solve(M, n, m, i, j - 1);
        int right = solve(M, n, m, i, j + 1);

        M[i][j] = value; 

        return value + max(up, max(down, max(left, right)));
    }

    int maxCoins(vector<vector<int>>& M, int n, int m) {
        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                ans = max(ans, solve(M, n, m, i, j));
            }
        }

        return ans;
    }
};

