class Solution {
    int CountWays(String S) {
        int n = S.length();
        int cnt=0;
        int[] arr = new int[n];
        HashSet<Character> st = new HashSet<Character>();
        for(int i=n-1;i>0;i--){
            st.add(S.charAt(i));
            arr[i] = st.size();
        }
        st.clear();
        for(int i=0;i<n-1;i++){
            st.add(S.charAt(i));
            cnt+= (st.size()==arr[i+1]?1:0);   
        }
        return cnt;
    }
};

