class Solution {
    static int disjointIntervals(ArrayList<ArrayList<Integer>> A, int N) {
        // code here
         A.sort((a,b)->{
            return a.get(1) - b.get(1);
        });
                
        int mx = 1;
        int e = A.get(0).get(1);
        for(int i = 1; i<N; i++){
            int s = A.get(i).get(0);
            int ee = A.get(i).get(1);
            
            if(s > e){
                e = ee;
                mx++;
            }
        }
        
        return mx;
    }
};

