class Solution {
    public ArrayList<ArrayList<Integer>> fourSum(int[] arr, int k) {
       
        ArrayList<ArrayList<Integer>>main=new ArrayList<>();
        Arrays.sort(arr);
        // code here
        int n=arr.length;
        int r;
        int f;
        for(int i=0;i<n-3;i++)
        {
            if( i>0 && arr[i]==arr[i-1])   continue;
          
            for(int j=i+1;j<n-2;j++)
            {
                 if(j>i+1&&arr[j]==arr[j-1]) continue;
           
                r=j+1;
                f=n-1;
                while(r < f)
                {
                    int  sum=arr[i]+arr[j]+arr[r]+arr[f];
                    if(sum==k)
                    {
                        ArrayList<Integer>list=new ArrayList<>();
                        list.add(arr[i]);
                        list.add(arr[j]);
                        list.add(arr[r]);
                        list.add(arr[f]);
                        main.add(list);
                        r++;
                        f--;
                          while(r<f && arr[r]==arr[r-1])r++;
                        while(r<f && arr[f]==arr[f+1])f--;
                    }
                    else if(sum < k)
                    {
                        r++;
                    }
                    else{
                        f--;
                    }
                }
                
            }
            
        }
        return main;
    }
