#include <bits/stdc++.h>
using namespace std;

// } Driver Code Ends
class Solution
{
  public:
     vector<int> smallestDifferenceTriplet(int a[], int b[], int c[], int n)
    {
        sort(a,a+n);
        sort(b,b+n);
        sort(c,c+n);
        int i=0,j=0,k=0,res=INT_MAX;
        vector<int>ans;
        
        while(i<n && j<n && k<n){
            int mini=min(a[i],min(b[j],c[k]));
            int maxi=max(a[i],max(b[j],c[k]));
            if(maxi-mini<res){
                res=maxi-mini;
                ans={a[i],b[j],c[k]};
            }
            if(mini==a[i]) i++;
            else if(mini==b[j]) j++;
            else k++;
        }
        sort(ans.begin(),ans.end(),greater<int>());
        return ans;      }
};

