    static int canReach(int[] A, int N) {
        // code here
        if(N == 1)
            return 1;
        if(A[0] == 0) 
            return 0;
        int hop = 0;
        for(int i = N-1; i > 0; i--) {
            if(A[i] == 0)
                hop+=1;
            else{
