    bool isValid(int x, int y, int n)
    {
        return x>=0 && y>=0&& x<n && y<n;
    }

    int find(int n, int x, int y, int k) {
        // Code here
        
        int arr[8][2] = {{2,1},{-2, 1}, {-2,-1},{2,-1},{1,2},{1,-2},{-1,-2},{-1,2}};
        
        
        vector<vector<vector<double>>> dp(n, vector<vector<double>>(n, vector<double>(k+1, 0)));
        
        
        for(int i =0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                dp[i][j][0] =1.00;
            }
        }
        
        for(int st = 1;st<=k;st++)
        {
            for(int i =0;i<n;i++)
            {
                for(int j=0;j<n;j++)
                {
                    double pr = 0.0;
                    for(auto a: arr)
                    {
                        int r = i+a[0];
                        int c = j+ a[1];
                        
                        if(isValid(r,c,n))
                        {
                            pr +=dp[r][c][st-1]; 
                        }
                    }
                    
                    dp[i][j][st] = pr/8.00;
                    
                }
            }
        }
        
        return dp[x][y][k] * 100;
    }
};

