    static String ReFormatString(String S, int K){
        
        
        String str[]=S.split("-");
        if(str.length==0)
        return "";
        int size=0;
        for(String s:str)
        {
            size=size+s.length();
        }
        
        int n=0,f=0;
        if(size%K==0)
        {
            n=K;
        }
        else
        {
            n=K;
            f=size%K;
        }
        String result="";
        String results="";
       
            if(f==0)
            {
                for(int i=0;i<S.length();i++)
                {
                    if(S.charAt(i)!='-' && result.length()!=n)
                    result=result+S.charAt(i);
                    
                    if(result.length()==n)
                    {
                    results=results+result+"-";
                    result="";
                    }
                    
                }
                return results.substring(0,results.length()-1).toUpperCase();
            }
            else
            {
                int flag=0;
                for(int i=0;i<S.length();i++)
                {
                    if(flag==0)
                    {
                    if(S.charAt(i)!='-' && result.length()!=f)
                    result=result+S.charAt(i);
                    
                    
                    if(result.length()==f)
                    {
                    results=results+result+"-";
                    result="";
                    flag=1;
                    }
                    }
                    else
