class Solution{
    Integer[][] mem;
    int ans = 0;
    
    int longestCommonSubstr(String S1, String S2, int n, int m){

        mem = new Integer[n+1][m+1];
        lcss(S1,S2, n, m);
        return ans;
    }
    
    
    int lcss(String x, String y, int n, int m){
        if(n == 0 || m == 0)
            return mem[n][m] = 0;
            
            
        if(mem[n][m] != null)
            return mem[n][m];
            

        lcss(x,y,n-1,m);
        lcss(x,y,n,m-1);
        
        int res = 0;
        if(x.charAt(n-1) == y.charAt(m-1)){
            res = 1 + lcss(x,y,n-1,m-1);
        }else{
            res = 0;
        }
        
        ans = Math.max(res, ans);
        return mem[n][m] = res;
        

    }
