#include <bits/stdc++.h>
using namespace std;


// } Driver Code Ends
class Solution
{
    public:
    //Function to find largest rectangular area possible in a given histogram.
    long long getMaxArea(long long arr[], int n)
    {
        // Your code here
        vector<long long> nsl,nsr;
        stack<long long> st;
        st.push(-1);
        for(int i=0;i<n;i++){
            while(st.top()!=-1 && arr[st.top()]>=arr[i])st.pop();
            nsl.push_back(st.top());
            st.push(i);
        }
        while(st.size())st.pop();
        st.push(n);
        for(int i=n-1;i>=0;i--){
            while(st.top()!=n && arr[st.top()]>=arr[i])st.pop();
            nsr.push_back(st.top());
            st.push(i);
        }
        long long res=0;
        reverse(begin(nsr),end(nsr));
        for(int i=0;i<n;i++){
            int b=nsr[i]-nsl[i]-1; 
            res=max(res,arr[i]*b);
        }
        return res;
    }
};


