#include <bits/stdc++.h>
using namespace std;

// } Driver Code Ends
//User function template for C++

class Solution{   
public:
    string solve(int arr[], int n) {
        sort(arr, arr+n);
        string a, b;
        for(int i = 0; i < n; i += 2) {
            a += (arr[i] + '0');
            if(i + 1 >= n) break;
            b += (arr[i + 1] + '0');
        }
        int i = 0;
        while(a[i] == '0') i++;
        a = a.substr(i);
        
        i = 0;
        while(b[i] == '0') i++;
        b = b.substr(i);
        
       reverse(a.begin(), a.end());
       reverse(b.begin(), b.end());
        
        string ans;
        int s = min(a.size(), b.size());
        string big = (s != a.size()) ? a : b;
        int carr = 0;
        for(int i = 0; i < s; i++) {
            int sum = a[i] + b[i] - 2 * '0' + carr;
            if(sum >= 10) {
                carr = sum / 10;
            } else carr = 0;
            sum %= 10;
            ans.push_back(sum + '0');
        }
        
        while(s < big.size()) {
            int sum = big[s] - '0' + carr;
            if(sum >= 10) {
                carr = sum / 10;
            } else carr = 0;
            sum %= 10;
            ans.push_back(sum + '0');
            s++;
        }
        
        if(carr != 0) ans.push_back(carr + '0');
        
        reverse(ans.begin(), ans.end());
        return ans == "" ? "0" : ans;
    }
};

