class Solution{

    private int solve(int coins[], int i, int val, int[][] dp){
        if(val == 0) return 0;
        if(val<0 || i == coins.length) return Integer.MAX_VALUE;
        if(dp[i][val] != 0) return dp[i][val];
        
        int val1 = solve(coins, i+1, val, dp);
        int val2 = solve(coins, i, val-coins[i], dp);
        if(val2 != Integer.MAX_VALUE) val2++;
        
        return dp[i][val] = Math.min(val1, val2);
    }
    
