class Solution {
  public:
    int numOfSubs(vector<string> v, string s) {
        // code here
    int n;
    n=v.size();
   
    vector<vector<int>> idx(26);
    for (int i = 0; i < s.length(); i++)
    {
        idx[s[i] - 'a'].push_back(i);
    }
    int cnt = 0;
    for (int i = 0; i < n; i++)
    {
        int len = v[i].length();
        int curr = -1;
        bool is_subs = true;
        for (int j = 0; j < len; j++)
        {
            auto x = upper_bound(idx[v[i][j] - 'a'].begin(), idx[v[i][j] - 'a'].end(), curr);
            if (x == idx[v[i][j] - 'a'].end())
            {
                is_subs = false;
                break;
            }
            curr = *x;
        }
        if (is_subs)
        {
            cnt++;
        }
    }
   return cnt;
    }
};

