class reading_4
{
    private char[] s;
    private int in, n;

    public reading_4(String g)
    {
        s = g.toCharArray();
        in = 0;
        n = s.length;
    }

    public int read4(char[] buf4)
    {
        int k = 0;

        for(int i = in; i < n; i++)
        {
            buf4[k++] = s[i];
            if(k == 4)
                break;
        }

        in += k;

        return k;
    }
}


