class Solution{
public:
	
	int findMaximum(int arr[], int n) {
	    // code here
	    int low = 0;
	    int high = n-1;
	    int result = 0;
	    int mid = 0;
	    
	    while(low < high){
	        mid = (low+high)/2;
	        
	        if(low == mid){
	            result = arr[high];
	            break;
	        }
	        
	        if(arr[mid] > arr[mid-1] && arr[mid] > arr[mid+1]){
	            result = arr[mid];
	            break;
	        }
	        else if(arr[mid] > arr[mid-1] && arr[mid] < arr[mid+1]){
	            low = mid;
	        }
	        else{
	            high = mid;
	        }
	    }
	    return result;
	}
};

