class Solution {
  public:
    int solve(int ind1, int ind2, string &s, string &t, vector<vector<int>> &dp) {
    if(ind1 == 0 || ind2 == 0)
        return max(ind1, ind2);
    
    if(dp[ind1][ind2] != -1) return dp[ind1][ind2];
    
    if(s[ind1 - 1] == t[ind2 - 1])
        return dp[ind1][ind2] = solve(ind1 - 1, ind2 - 1, s, t, dp);
    
    int insert = 1 + solve(ind1, ind2 - 1, s, t, dp);
    int remove = 1 + solve(ind1 - 1, ind2, s, t, dp);
    int replace = 1 + solve(ind1 - 1, ind2 - 1, s, t, dp);
    
    return dp[ind1][ind2] = min({insert, remove, replace});
}

int editDistance(string s, string t) {
    int n = s.length(), m = t.length();
    vector<vector<int>> dp(n + 1, vector<int>(m + 1, -1));
    return solve(n, m, s, t, dp);
}


};





