    def graycode(self,m):
        if m == 1:
            return ['0', '1']
        ans1 = self.graycode(m-1)
        ans = []
        for sa in ans1:
            ans.append('0'+sa)
        for sa in ans1[::-1]:
            ans.append('1'+sa)
