class Solution{
    static int isValid(int mat[][]){
        // code here
        
         for (int row = 0; row < 9; row++) {
            for (int col = 0; col < 9; col++) {
                int num = mat[row][col];
                if (num != 0) {
                    // Check if the current number is valid in its row
                    for (int j = 0; j < 9; j++) {
                        if (j != col && mat[row][j] == num) {
                            return 0;
                        }
                    }
                    
                    // Check if the current number is valid in its column
                    for (int i = 0; i < 9; i++) {
                        if (i != row && mat[i][col] == num) {
                            return 0;
                        }
                    }
                    
                    // Check if the current number is valid in its 3x3 sub-grid
                    int startRow = row / 3 * 3;
                    int startCol = col / 3 * 3;
                    for (int i = startRow; i < startRow + 3; i++) {
                        for (int j = startCol; j < startCol + 3; j++) {
                            if ((i != row || j != col) && mat[i][j] == num) {
                                return 0;
                            }
                        }
                    }
                }
            }
        }
        return 1;
    }
}

