class Solution
{
    //Function to return the largest possible number of n digits
    //with sum equal to given sum.
    static String largestNumber(int n, int sum)
    {
        if((n*9)<sum)return "-1";
        String ans="";
        while(sum>=1){
            if(sum>=9){
                ans+="9";
                sum-=9;
            }
            else{
                ans+=String.valueOf(sum);
                break;
            }
        }
        if(ans.length()<n){
            int idx=0;
            for(int i=0; i<ans.length(); i++){
                if(ans.charAt(i)>'0')idx++;
            }
            while(idx!=n){
                ans+="0";
                idx++;
            }
        }
        
        return ans;
    }
}

