/*  Function to calculate the longest consecutive ones
*   N: given input to calculate the longest consecutive ones
*/
class Solution
{
    public:
    int maxConsecutiveOnes(int N)
    {
        int ones=0;
        int lastones=0;
       while(N!=0)
        {
        if(N%2==0)
        {
            if(lastones<ones)
            {
            lastones=ones;
            ones=0;
            }
            else
            {
            ones=0;
            }
            
            
        }
        else if(N%2==1)
        {
            ones++;
            
        }
        else
        {
            break;
        }
        
        N=N/2;
        }
        
        if(lastones>=ones)
        {
            return lastones;
        }
        else
        {
            return ones;
        }
        
    }
};


