class Solution
{
    public int longestPalinSubseq(String S)
    {
        StringBuilder original = new StringBuilder();
        original.append(S);
        String reverse = original.reverse().toString();
        int dp[][] = new int[S.length()+1][reverse.length()+1];
        for(int i = 0;i <S.length()+1;i++){
            for(int j = 0;j < reverse.length()+1;j++){
                dp[i][j] = -1;
            }
        }
        return lcsutil(S.length(),reverse.length(),S,reverse,dp);
    }
    static int lcsutil(int x, int y, String s1, String s2,int[][]dp){
        if(x == 0||y == 0) return 0;
        if(dp[x][y] != -1) return dp[x][y];
        if(s1.charAt(x-1) == s2.charAt(y-1)){
            dp[x][y] = 1+lcsutil(x-1,y-1,s1,s2,dp);
            return dp[x][y];
        }
        else{
            return dp[x][y] = Math.max(lcsutil(x-1,y,s1,s2,dp),lcsutil(x,y-1,s1,s2,dp));
        }
    }
    
}

