//User function Template for Java

class Solution { 
    static int catchThieves(char arr[], int n, int k) { 
        char dupli[] = arr.clone();
        int ans = 0;
        for(int i=0;i<n;i++){
            if(arr[i]=='P'){
                boolean found = false;
                int j = Math.max(i-k,0);
                while(j<i){
                    if(dupli[j]=='T'){
                        ans++;
                        dupli[j] = 'S';
                        found = true;
                        break;
                    }
                    j++;
                }
                if(!found){
                    j = Math.min(i+k,n-1);
                    int p = i+1;
                    while(p<=j){
                        if(dupli[p]=='T'){
                            ans++;
                            dupli[p] = 'S';
                            found = true;
                            break;
                        }
                        p++;
                    }
                }
            }
        }
        return ans;
	} 
} 



