class Solution 
{
    //Function to reverse words in a given string.
    String reverseWords(String S)
    {
     
        String stringSepArray[]=new String[0];
        stringSepArray= S.split("\\.");
        String finalString="";
        for (int j=stringSepArray.length-1;j>=0;j--){
            finalString=finalString+stringSepArray[j];
              finalString=finalString+".";

        }
        finalString=finalString.substring(0,finalString.length()-1);
        return finalString;
    }
}

