class Solution:
    def romanToDecimal(self, S): 
        # code here
        arr=[]
        sum=0
        for i in S:
            if(i=="I"):
                arr.append(1)
            elif(i=="X"):
                arr.append(10)
            elif(i=="V"):
                arr.append(5)    
            elif(i=="L"):
                arr.append(50)
            elif(i=="C"):
                arr.append(100)
            elif(i=="D"):
                arr.append(500)
            elif(i=="M"):
                arr.append(1000)    
#print(arr)   
        for i in range(0,len(arr)):
            if(i>0 and arr[i]>arr[i-1]):
                sum+=arr[i]-2*arr[i-1]
            else:
                 sum+=arr[i]
        return(sum if sum>0 else -(sum)) 
            
            


