import java.io.*;
import java.util.*;
import java.lang.*; 

class GFG{
    public static void main(String args[]) throws IOException { 
        Scanner sc = new Scanner(System.in);
        int t = sc.nextInt();
        while(t > 0){
        	int n = sc.nextInt();
        	int target = sc.nextInt();
        	int[] array = new int[n];
        	for(int i=0; i<n; i++)
        		array[i] = sc.nextInt();

            Solution ob = new Solution();
            System.out.println(ob.threeSumClosest(array,target));
            t--;
        }
    } 
} 

// } Driver Code Ends


//User function Template for Java

class Solution 
{ 
    static int threeSumClosest(int[] array, int target)  
	{ 
        // code here
	Arrays.sort(array);
        int sum=array[0]+array[1]+array[array.length-1];
        for(int i=0;i<array.length-1;i++)
        {
            int left=i+1;
            int right=array.length-1;
            
            while(left<right)
            {
                int csum=array[i]+array[left]+array[right];
                
                if(Math.abs(csum-target)<Math.abs(sum-target))
                {
                sum=csum;
                }
                else if(Math.abs(csum-target)==Math.abs(sum-target) && csum>sum)
                      sum=csum;
                
                if(csum==target)
                return csum;
                
                else if(csum<target)
                left++;
                else
                right--;
            }
        }
        return sum;
    } 
}

